let num1=Number(prompt("Enter first number: "))
let num2=Number(prompt("Enter second number: "))
let operation=prompt("Enter operation (+, -, *, /):")

if (operation === "+") {
    console.log("your result is " + (num1 + num2))}
    
else if (operation === "-") {
    console.log("your result is " + (num1 - num2))}
    
else if (operation === "*") {
    console.leg("your result is " + (num1 * num2))} 
    
else if (operation === "/") {
    console.log("your result is " + (num1 / num2))
}